import React from 'react';
import {connect} from 'react-redux';
import {signIn, signOut} from '../actions';

class GoogleAuth extends React.Component{
    state = {isSignedIn: null};
    componentDidMount(){
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '14658180405-bvmdgofm8pafr6nt55kju93ikjrb37lb.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({
                    isSignedIn: this.auth.isSignedIn.get()
                })
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = (isSignedIn) =>{
        this.setState({isSignedIn:this.auth.isSignedIn.get()})
    };
    onSignInClick = () =>{
        this.auth.signIn();
    };
    onSignOutClick = () =>{
        this.auth.signOut();
    };

    renderAuthButton(){
        if(this.state.isSignedIn === null){
            return null;
        } else if(this.state.isSignedIn){
            return <button onClick={this.onSignOutClick} className="ui red google button">
                <i className="google icon">
                    Sign Out
                </i>
            </button>
        } else {
            return <button className="ui green google button" onClick={this.onSignInClick} >
                <i className="google icon">
                    Sign In with google
                </i>
            </button>
        }
    }

    render(){
        return(
            <div>
               {this.renderAuthButton()}
            </div>
        );
    }
}

export default connect(null,{sigIn,signOut})(GoogleAuth);
